import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/otpService.ts.content:
import { randomInt } from "crypto";
import { db } from "./db";
import { otpCodes, users } from "@shared/schema";
import { eq, and, lt, gt } from "drizzle-orm";

interface OtpServiceConfig {
  emailProvider?: 'console' | 'sendgrid'; // We'll use console for now, can be extended
  smsProvider?: 'console' | 'twilio'; // We'll use console for now, can be extended
}

export class OtpService {
  private config: OtpServiceConfig;

  constructor(config: OtpServiceConfig = {}) {
    this.config = {
      emailProvider: 'console',
      smsProvider: 'console',
      ...config
    };
  }

  // Generate a 6-digit OTP
  private generateOtp(): string {
    return randomInt(100000, 999999).toString();
  }

  // Validate email format (Gmail only for now)
  private isValidEmail(email: string): boolean {
    const emailRegex = /^[a-zA-Z0-9._%+-]+@gmail\.com$/;
    return emailRegex.test(email);
  }

  // Validate phone format (simple international format)
  private isValidPhone(phone: string): boolean {
    const phoneRegex = /^\+[1-9]\d{1,14}$/; // E.164 format
    return phoneRegex.test(phone);
  }

  // Check rate limiting (max 3 OTP requests per identifier in 5 minutes)
  private async checkRateLimit(identifier: string, ipAddress: string): Promise<boolean> {
    const fiveMinutesAgo = new Date(Date.now() - 5 * 60 * 1000);

    const recentOtps = await db
      .select()
      .from(otpCodes)
      .where(
        and(
          eq(otpCodes.identifier, identifier),
          gt(otpCodes.createdAt, fiveMinutesAgo)
        )
      );

    return recentOtps.length < 3;
  }

  // Send OTP via email (console implementation for now)
  private async sendEmailOtp(email: string, otp: string): Promise<boolean> {
    try {
      // For development, we'll just log to console
      // In production, integrate with SendGrid, AWS SES, etc.
      console.log(`\nðŸ” OTP EMAIL for ${email}: ${otp}\n`);

      // Simulate email sending delay
      await new Promise(resolve => setTimeout(resolve, 100));

      return true;
    } catch (error) {
      console.error('Failed to send email OTP:', error);
      return false;
    }
  }

  // Send OTP via SMS (console implementation for now)
  private async sendSmsOtp(phone: string, otp: string): Promise<boolean> {
    try {
      // For development, we'll just log to console
      // In production, integrate with Twilio, etc.
      console.log(`\nðŸ“± SMS OTP for ${phone}: ${otp}\n`);

      // Simulate SMS sending delay
      await new Promise(resolve => setTimeout(resolve, 100));

      return true;
    } catch (error) {
      console.error('Failed to send SMS OTP:', error);
      return false;
    }
  }

  // Send OTP to email or phone
  async sendOtp(
    identifier: string,
    type: 'email' | 'phone',
    ipAddress: string
  ): Promise<{ success: boolean; message: string }> {
    try {
      // Validate identifier format
      if (type === 'email' && !this.isValidEmail(identifier)) {
        return { success: false, message: 'Please enter a valid Gmail address' };
      }

      if (type === 'phone' && !this.isValidPhone(identifier)) {
        return { success: false, message: 'Please enter a valid phone number with country code (e.g., +1234567890)' };
      }

      // Check rate limiting
      const canSend = await this.checkRateLimit(identifier, ipAddress);
      if (!canSend) {
        return { success: false, message: 'Too many OTP requests. Please wait 5 minutes before trying again.' };
      }

      // Generate OTP
      const otp = this.generateOtp();
      const expiresAt = new Date(Date.now() + 2 * 60 * 1000); // 2 minutes

      // Store OTP in database
      await db.insert(otpCodes).values({
        identifier,
        identifierType: type,
        otp,
        ipAddress,
        expiresAt,
      });

      // Send OTP
      let sent = false;
      if (type === 'email') {
        sent = await this.sendEmailOtp(identifier, otp);
      } else {
        sent = await this.sendSmsOtp(identifier, otp);
      }

      if (!sent) {
        return { success: false, message: 'Failed to send OTP. Please try again.' };
      }

      return { 
        success: true, 
        message: `OTP sent to your ${type === 'email' ? 'email' : 'phone'}. Valid for 2 minutes.` 
      };

    } catch (error) {
      console.error('Error sending OTP:', error);
      return { success: false, message: 'Failed to send OTP. Please try again.' };
    }
  }

  // Verify OTP and authenticate user
  async verifyOtp(
    identifier: string,
    otp: string,
    type: 'email' | 'phone'
  ): Promise<{ 
    success: boolean; 
    message: string; 
    user?: any;
    isNewUser?: boolean;
  }> {
    try {
      // Find the most recent valid OTP for this identifier
      const otpRecord = await db
        .select()
        .from(otpCodes)
        .where(
          and(
            eq(otpCodes.identifier, identifier),
            eq(otpCodes.identifierType, type),
            eq(otpCodes.used, false),
            gt(otpCodes.expiresAt, new Date())
          )
        )
        .orderBy(otpCodes.createdAt)
        .limit(1);

      if (!otpRecord.length) {
        return { success: false, message: 'Invalid or expired OTP. Please request a new one.' };
      }

      const record = otpRecord[0];

      // Check attempts (max 3)
      if ((record.attempts || 0) >= 3) {
        await db
          .update(otpCodes)
          .set({ used: true })
          .where(eq(otpCodes.id, record.id));

        return { success: false, message: 'Too many failed attempts. Please request a new OTP.' };
      }

      // Check OTP match
      if (record.otp !== otp) {
        await db
          .update(otpCodes)
          .set({ attempts: (record.attempts || 0) + 1 })
          .where(eq(otpCodes.id, record.id));

        return { success: false, message: 'Invalid OTP. Please check and try again.' };
      }

      // Mark OTP as used
      await db
        .update(otpCodes)
        .set({ used: true })
        .where(eq(otpCodes.id, record.id));

      // Find or create user
      let user;
      let isNewUser = false;

      if (type === 'email') {
        user = await db
          .select()
          .from(users)
          .where(eq(users.email, identifier))
          .limit(1);
      } else {
        user = await db
          .select()
          .from(users)
          .where(eq(users.phone, identifier))
          .limit(1);
      }

      if (!user.length) {
        // Create new user
        const newUserData = type === 'email' 
          ? { email: identifier }
          : { phone: identifier };

        const newUser = await db
          .insert(users)
          .values({
            ...newUserData,
            balance: 1000, // Welcome bonus
            lastLoginAt: new Date(),
          })
          .returning();

        user = newUser;
        isNewUser = true;
      } else {
        // Update last login
        await db
          .update(users)
          .set({ lastLoginAt: new Date() })
          .where(eq(users.id, user[0].id));
      }

      return {
        success: true,
        message: isNewUser 
          ? 'Welcome! Your account has been created with 1000 bonus points!' 
          : 'Login successful!',
        user: user[0],
        isNewUser
      };

    } catch (error) {
      console.error('Error verifying OTP:', error);
      return { success: false, message: 'Failed to verify OTP. Please try again.' };
    }
  }

  // Clean up expired OTPs (run periodically)
  async cleanupExpiredOtps(): Promise<void> {
    try {
      await db
        .delete(otpCodes)
        .where(lt(otpCodes.expiresAt, new Date()));
    } catch (error) {
      console.error('Error cleaning up expired OTPs:', error);
    }
  }
}

export const otpService = new OtpService();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET || "default-dev-secret-change-in-production",
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: process.env.NODE_ENV === "production",
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { insertTransactionSchema, insertGameRoundSchema, insertAviatorBetSchema, sendOtpSchema, verifyOtpSchema } from "@shared/schema";
import { randomUUID } from "crypto";
import { otpService } from "./otpService";
import jwt from "jsonwebtoken";

interface ExtendedWebSocket extends WebSocket {
  userId?: string;
  isAlive?: boolean;
}

let currentAviatorRound: {
  roundId: string;
  status: 'betting' | 'flying' | 'crashed';
  multiplier: number;
  crashPoint: number;
  startTime?: Date;
} | null = null;

let aviatorInterval: NodeJS.Timeout | null = null;
const connectedClients = new Set<ExtendedWebSocket>();

// JWT Secret for OTP authentication (in production, use environment variable)
const JWT_SECRET = process.env.JWT_SECRET || 'your-secret-key-change-in-production';

// OTP Auth middleware for JWT tokens
const otpAuthenticated = (req: any, res: any, next: any) => {
  const authHeader = req.headers.authorization;
  const token = authHeader && authHeader.split(' ')[1]; // Bearer TOKEN

  if (!token) {
    return res.status(401).json({ message: 'Access token required' });
  }

  try {
    const decoded = jwt.verify(token, JWT_SECRET) as any;
    req.user = { id: decoded.userId, email: decoded.email, phone: decoded.phone };
    next();
  } catch (error) {
    return res.status(403).json({ message: 'Invalid or expired token' });
  }
};

export async function registerRoutes(app: Express): Promise<Server> {
  // Legacy Replit Auth setup (keeping for backwards compatibility)
  await setupAuth(app);

  // OTP Authentication endpoints
  app.post('/api/auth/send-otp', async (req, res) => {
    try {
      const validatedData = sendOtpSchema.parse(req.body);
      const clientIp = req.ip || req.connection.remoteAddress || '127.0.0.1';

      const result = await otpService.sendOtp(
        validatedData.identifier,
        validatedData.type,
        clientIp
      );

      res.status(result.success ? 200 : 400).json(result);
    } catch (error: any) {
      console.error('Send OTP error:', error);
      res.status(400).json({ 
        success: false, 
        message: error.message || 'Invalid request data' 
      });
    }
  });

  app.post('/api/auth/verify-otp', async (req, res) => {
    try {
      const validatedData = verifyOtpSchema.parse(req.body);

      const result = await otpService.verifyOtp(
        validatedData.identifier,
        validatedData.otp,
        validatedData.type
      );

      if (result.success && result.user) {
        // Generate JWT token
        const token = jwt.sign(
          { 
            userId: result.user.id, 
            email: result.user.email, 
            phone: result.user.phone 
          },
          JWT_SECRET,
          { expiresIn: '7d' } // Token valid for 7 days
        );

        res.json({
          ...result,
          token,
          user: result.user
        });
      } else {
        res.status(400).json(result);
      }
    } catch (error: any) {
      console.error('Verify OTP error:', error);
      res.status(400).json({ 
        success: false, 
        message: error.message || 'Invalid request data' 
      });
    }
  });

  // User info endpoint (supports both auth methods)
  app.get('/api/auth/user', (req: any, res: any, next: any) => {
    // Try OTP auth first, fallback to Replit auth
    const authHeader = req.headers.authorization;
    if (authHeader && authHeader.startsWith('Bearer ')) {
      return otpAuthenticated(req, res, next);
    } else {
      return isAuthenticated(req, res, next);
    }
  }, async (req: any, res) => {
    try {
      // Handle both auth types
      const userId = req.user.id || req.user.claims?.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Balance routes (support both auth methods)
  app.get('/api/balance', (req: any, res: any, next: any) => {
    const authHeader = req.headers.authorization;
    if (authHeader && authHeader.startsWith('Bearer ')) {
      return otpAuthenticated(req, res, next);
    } else {
      return isAuthenticated(req, res, next);
    }
  }, async (req: any, res) => {
    try {
      const userId = req.user.id || req.user.claims?.sub;
      const balance = await storage.getUserBalance(userId);
      res.json({ balance });
    } catch (error) {
      console.error("Error fetching balance:", error);
      res.status(500).json({ message: "Failed to fetch balance" });
    }
  });

  // Top-up routes (support both auth methods)
  app.post('/api/topup', (req: any, res: any, next: any) => {
    const authHeader = req.headers.authorization;
    if (authHeader && authHeader.startsWith('Bearer ')) {
      return otpAuthenticated(req, res, next);
    } else {
      return isAuthenticated(req, res, next);
    }
  }, async (req: any, res) => {
    try {
      const userId = req.user.id || req.user.claims?.sub;
      const { amount, package: packageType } = req.body;

      // Validate package
      const packages: Record<string, { price: number; points: number }> = {
        starter: { price: 50, points: 500 },
        value: { price: 100, points: 1100 },
        premium: { price: 200, points: 2500 },
      };

      if (!packages[packageType]) {
        return res.status(400).json({ message: "Invalid package" });
      }

      const pkg = packages[packageType];
      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Create transaction
      const transaction = await storage.createTransaction({
        userId,
        type: 'topup',
        amount: pkg.points,
        status: 'completed',
        paymentId: randomUUID(),
      });

      // Update user balance
      const newBalance = (user.balance || 0) + pkg.points;
      await storage.updateUserBalance(userId, newBalance);

      res.json({ success: true, transaction, newBalance });
    } catch (error) {
      console.error("Error processing top-up:", error);
      res.status(500).json({ message: "Failed to process top-up" });
    }
  });

  // Game history routes
  app.get('/api/game-history', (req: any, res: any, next: any) => {
    const authHeader = req.headers.authorization;
    if (authHeader && authHeader.startsWith('Bearer ')) {
      return otpAuthenticated(req, res, next);
    } else {
      return isAuthenticated(req, res, next);
    }
  }, async (req: any, res) => {
    try {
      const userId = req.user.id || req.user.claims?.sub;
      const gameHistory = await storage.getUserGameHistory(userId, 20);
      res.json(gameHistory);
    } catch (error) {
      console.error("Error fetching game history:", error);
      res.status(500).json({ message: "Failed to fetch game history" });
    }
  });

  // Transaction history routes
  app.get('/api/transactions', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const transactions = await storage.getUserTransactions(userId, 20);
      res.json(transactions);
    } catch (error) {
      console.error("Error fetching transactions:", error);
      res.status(500).json({ message: "Failed to fetch transactions" });
    }
  });

  // Color game routes
  app.post('/api/color-game/play', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { selectedColors, betAmount } = req.body;

      // Validate input
      if (!Array.isArray(selectedColors) || selectedColors.length === 0 || selectedColors.length > 3) {
        return res.status(400).json({ message: "Invalid color selection" });
      }

      // Validate bet amount based on selection count
      const pricing: Record<number, { entry: number; win: number }> = {
        1: { entry: 20, win: 40 },
        2: { entry: 30, win: 60 },
        3: { entry: 45, win: 90 }
      };

      const count = selectedColors.length;
      if (betAmount !== pricing[count].entry) {
        return res.status(400).json({ message: "Invalid bet amount" });
      }

      const user = await storage.getUser(userId);
      if (!user || (user.balance || 0) < betAmount) {
        return res.status(400).json({ message: "Insufficient balance" });
      }

      // Generate winning color (1-6)
      const winningColor = Math.floor(Math.random() * 6) + 1;
      const isWin = selectedColors.includes(winningColor);
      const winAmount = isWin ? pricing[count].win : 0;

      // Create game round
      const gameRound = await storage.createGameRound({
        userId,
        gameType: 'color_game',
        betAmount,
        result: isWin ? 'win' : 'loss',
        winAmount,
        gameData: {
          selectedColors,
          winningColor,
        },
      });

      // Create bet transaction
      await storage.createTransaction({
        userId,
        type: 'game_bet',
        amount: -betAmount,
        gameType: 'color_game',
        gameRoundId: gameRound.id,
      });

      // Create win transaction if won
      if (isWin) {
        await storage.createTransaction({
          userId,
          type: 'game_win',
          amount: winAmount,
          gameType: 'color_game',
          gameRoundId: gameRound.id,
        });
      }

      // Update user balance and stats
      const newBalance = (user.balance || 0) - betAmount + winAmount;
      await storage.updateUserBalance(userId, newBalance);
      await storage.updateUserStats(userId, isWin, winAmount);

      res.json({
        result: isWin ? 'win' : 'loss',
        winningColor,
        winAmount,
        newBalance,
        gameRound,
      });
    } catch (error) {
      console.error("Error playing color game:", error);
      res.status(500).json({ message: "Failed to play color game" });
    }
  });

  // Aviator game routes
  app.get('/api/aviator/current-game', async (req, res) => {
    try {
      if (!currentAviatorRound) {
        return res.json({ game: null });
      }

      res.json({
        game: {
          roundId: currentAviatorRound.roundId,
          status: currentAviatorRound.status,
          multiplier: currentAviatorRound.multiplier,
          startTime: currentAviatorRound.startTime,
        }
      });
    } catch (error) {
      console.error("Error fetching current aviator game:", error);
      res.status(500).json({ message: "Failed to fetch current game" });
    }
  });

  app.post('/api/aviator/place-bet', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { betAmount, autoCashOut } = req.body;

      if (!currentAviatorRound || currentAviatorRound.status !== 'betting') {
        return res.status(400).json({ message: "No active betting round" });
      }

      if (betAmount < 10 || betAmount > 1000) {
        return res.status(400).json({ message: "Invalid bet amount" });
      }

      const user = await storage.getUser(userId);
      if (!user || (user.balance || 0) < betAmount) {
        return res.status(400).json({ message: "Insufficient balance" });
      }

      // Check if user already has a bet in this round
      const existingBet = await storage.getUserAviatorBet(userId, currentAviatorRound.roundId);
      if (existingBet) {
        return res.status(400).json({ message: "Already placed bet in this round" });
      }

      // Create aviator bet
      const bet = await storage.createAviatorBet({
        userId,
        roundId: currentAviatorRound.roundId,
        betAmount,
        autoCashOut: autoCashOut || null,
      });

      // Create bet transaction
      await storage.createTransaction({
        userId,
        type: 'game_bet',
        amount: -betAmount,
        gameType: 'aviator',
        gameRoundId: currentAviatorRound.roundId,
      });

      // Update user balance
      const newBalance = (user.balance || 0) - betAmount;
      await storage.updateUserBalance(userId, newBalance);

      // Broadcast bet to all clients
      broadcastToClients({
        type: 'bet_placed',
        data: {
          userId,
          betAmount,
          autoCashOut,
        }
      });

      res.json({ success: true, bet, newBalance });
    } catch (error) {
      console.error("Error placing aviator bet:", error);
      res.status(500).json({ message: "Failed to place bet" });
    }
  });

  app.post('/api/aviator/cash-out', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;

      if (!currentAviatorRound || currentAviatorRound.status !== 'flying') {
        return res.status(400).json({ message: "Cannot cash out now" });
      }

      const bet = await storage.getUserAviatorBet(userId, currentAviatorRound.roundId);
      if (!bet || bet.status !== 'active') {
        return res.status(400).json({ message: "No active bet found" });
      }

      const winAmount = Math.floor(bet.betAmount * currentAviatorRound.multiplier);

      // Update bet
      await storage.updateAviatorBet(bet.id, {
        cashOutAt: currentAviatorRound.multiplier.toString(),
        status: 'cashed_out',
        winAmount,
      });

      // Create win transaction
      await storage.createTransaction({
        userId,
        type: 'game_win',
        amount: winAmount,
        gameType: 'aviator',
        gameRoundId: currentAviatorRound.roundId,
      });

      const user = await storage.getUser(userId);
      if (user) {
        await storage.updateUserBalance(userId, (user.balance || 0) + winAmount);
        await storage.updateUserStats(userId, true, winAmount);
      }

      // Broadcast cash out to all clients
      broadcastToClients({
        type: 'cash_out',
        data: {
          userId,
          multiplier: currentAviatorRound.multiplier,
          winAmount,
        }
      });

      res.json({ success: true, winAmount, multiplier: currentAviatorRound.multiplier });
    } catch (error) {
      console.error("Error cashing out:", error);
      res.status(500).json({ message: "Failed to cash out" });
    }
  });

  app.post('/api/aviator/take-winnings', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;

      // Get the most recent cashed out bet for the user
      const recentBets = await storage.getUserAviatorBets(userId, 5); // Get last 5 bets
      const cashedOutBet = recentBets.find(bet => bet.status === 'cashed_out' && (bet.winAmount || 0) > 0);

      if (!cashedOutBet) {
        return res.status(400).json({ message: "No winnings to collect" });
      }

      res.json({ 
        success: true, 
        winnings: cashedOutBet.winAmount,
        betAmount: cashedOutBet.betAmount,
        multiplier: parseFloat(cashedOutBet.cashOutAt || '0')
      });
    } catch (error) {
      console.error("Error taking winnings:", error);
      res.status(500).json({ message: "Failed to collect winnings" });
    }
  });

  const httpServer = createServer(app);

  // WebSocket setup for Aviator game
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  wss.on('connection', (ws: ExtendedWebSocket) => {
    ws.isAlive = true;
    connectedClients.add(ws);

    ws.on('pong', () => {
      ws.isAlive = true;
    });

    ws.on('message', async (message) => {
      try {
        const data = JSON.parse(message.toString());

        if (data.type === 'join') {
          ws.userId = data.userId;

          // Send current game state
          if (currentAviatorRound) {
            ws.send(JSON.stringify({
              type: 'game_state',
              data: {
                roundId: currentAviatorRound.roundId,
                status: currentAviatorRound.status,
                multiplier: currentAviatorRound.multiplier,
                startTime: currentAviatorRound.startTime,
              }
            }));
          }
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
      }
    });

    ws.on('close', () => {
      connectedClients.delete(ws);
    });
  });

  // Ping clients to keep connections alive
  setInterval(() => {
    wss.clients.forEach((ws: ExtendedWebSocket) => {
      if (ws.isAlive === false) {
        ws.terminate();
        connectedClients.delete(ws);
        return;
      }
      ws.isAlive = false;
      ws.ping();
    });
  }, 30000);

  function broadcastToClients(message: any) {
    connectedClients.forEach((client) => {
      if (client.readyState === WebSocket.OPEN) {
        client.send(JSON.stringify(message));
      }
    });
  }

  // Aviator game loop
  function startAviatorRound() {
    const roundId = randomUUID();
    const crashPoint = 1.01 + Math.random() * 8.99; // Random crash point between 1.01 and 10.00

    currentAviatorRound = {
      roundId,
      status: 'betting',
      multiplier: 1.00,
      crashPoint: parseFloat(crashPoint.toFixed(2)),
    };

    console.log(`Starting new Aviator round ${roundId} with crash point ${currentAviatorRound.crashPoint}x`);

    // Save game state to database
    storage.createAviatorGameState({
      roundId,
      status: 'betting',
      multiplier: '1.00',
      crashPoint: crashPoint.toString(),
    }).catch(err => console.error('Error saving game state:', err));

    // Broadcast betting phase
    broadcastToClients({
      type: 'round_started',
      data: {
        roundId,
        status: 'betting',
      }
    });

    // Betting phase (5 seconds)
    setTimeout(() => {
      if (currentAviatorRound?.roundId === roundId) {
        startFlying();
      }
    }, 5000);
  }

  function startFlying() {
    if (!currentAviatorRound) return;

    currentAviatorRound.status = 'flying';
    currentAviatorRound.startTime = new Date();

    // Update database
    storage.updateAviatorGameState(currentAviatorRound.roundId, {
      status: 'flying',
      startTime: currentAviatorRound.startTime,
    });

    // Broadcast flying phase
    broadcastToClients({
      type: 'flying_started',
      data: {
        roundId: currentAviatorRound.roundId,
        startTime: currentAviatorRound.startTime,
      }
    });

    // Flying phase - increment multiplier
    aviatorInterval = setInterval(async () => {
      if (!currentAviatorRound || currentAviatorRound.status !== 'flying') {
        if (aviatorInterval) clearInterval(aviatorInterval);
        return;
      }

      currentAviatorRound.multiplier += 0.01;

      // Check for crash
      if (currentAviatorRound.multiplier >= currentAviatorRound.crashPoint) {
        crashAviator();
        return;
      }

      // Check for auto cash outs
      const roundBets = await storage.getAviatorBetsForRound(currentAviatorRound.roundId);
      for (const bet of roundBets) {
        if (bet.status === 'active' && bet.autoCashOut && currentAviatorRound.multiplier >= parseFloat(bet.autoCashOut.toString())) {
          const winAmount = Math.floor(bet.betAmount * currentAviatorRound.multiplier);

          await storage.updateAviatorBet(bet.id, {
            cashOutAt: currentAviatorRound.multiplier.toString(),
            status: 'cashed_out',
            winAmount,
          });

          await storage.createTransaction({
            userId: bet.userId,
            type: 'game_win',
            amount: winAmount,
            gameType: 'aviator',
            gameRoundId: currentAviatorRound.roundId,
          });

          const user = await storage.getUser(bet.userId);
          if (user) {
            await storage.updateUserBalance(bet.userId, (user.balance || 0) + winAmount);
            await storage.updateUserStats(bet.userId, true, winAmount);
          }

          broadcastToClients({
            type: 'auto_cash_out',
            data: {
              userId: bet.userId,
              multiplier: currentAviatorRound.multiplier,
              winAmount,
            }
          });
        }
      }

      // Broadcast multiplier update
      broadcastToClients({
        type: 'multiplier_update',
        data: {
          multiplier: parseFloat(currentAviatorRound.multiplier.toFixed(2)),
        }
      });
    }, 100);
  }

  async function crashAviator() {
    if (aviatorInterval) {
      clearInterval(aviatorInterval);
      aviatorInterval = null;
    }

    if (!currentAviatorRound) return;

    const finalMultiplier = currentAviatorRound.multiplier;
    currentAviatorRound.status = 'crashed';

    // Update database
    await storage.updateAviatorGameState(currentAviatorRound.roundId, {
      status: 'crashed',
      multiplier: finalMultiplier.toString(),
      crashTime: new Date(),
    });

    // Handle losing bets
    const roundBets = await storage.getAviatorBetsForRound(currentAviatorRound.roundId);
    for (const bet of roundBets) {
      if (bet.status === 'active') {
        await storage.updateAviatorBet(bet.id, {
          status: 'crashed',
        });

        // Update user stats (loss)
        await storage.updateUserStats(bet.userId, false);
      }
    }

    // Broadcast crash
    broadcastToClients({
      type: 'crashed',
      data: {
        multiplier: parseFloat(finalMultiplier.toFixed(2)),
        crashPoint: currentAviatorRound.crashPoint,
      }
    });

    // Start new round after 3 seconds
    setTimeout(() => {
      startAviatorRound();
    }, 3000);
  }

  // Start first aviator round immediately
  console.log('Starting Aviator game engine...');
  startAviatorRound();

  return httpServer;
}

