client/src/pages/otp-login.tsx.content:
import { useState, useEffect } from "react";
import { useLocation, useNavigate } from "wouter";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Loader2, Mail, Phone, Lock, Sparkles } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface SendOtpResponse {
  success: boolean;
  message: string;
}

interface VerifyOtpResponse {
  success: boolean;
  message: string;
  token?: string;
  user?: any;
  isNewUser?: boolean;
}

export default function OtpLogin() {
  const [location, setLocation] = useLocation();
  const navigate = useNavigate();
  const { toast } = useToast();

  const [authType, setAuthType] = useState<"email" | "phone">("email");
  const [identifier, setIdentifier] = useState("");
  const [otp, setOtp] = useState("");
  const [step, setStep] = useState<"identifier" | "otp">("identifier");
  const [timer, setTimer] = useState(0);

  // Timer for OTP resend
  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (timer > 0) {
      interval = setInterval(() => {
        setTimer((prev) => prev - 1);
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [timer]);

  // Send OTP mutation
  const sendOtpMutation = useMutation({
    mutationFn: async (data: { identifier: string; type: "email" | "phone" }) => {
      const response = await apiRequest('/api/auth/send-otp', {
        method: 'POST',
        body: JSON.stringify(data),
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to send OTP');
      }

      return response.json() as Promise<SendOtpResponse>;
    },
    onSuccess: (data) => {
      if (data.success) {
        setStep("otp");
        setTimer(120); // 2 minutes
        toast({
          title: "OTP Sent!",
          description: data.message,
        });
      } else {
        toast({
          title: "Error",
          description: data.message,
          variant: "destructive",
        });
      }
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Verify OTP mutation
  const verifyOtpMutation = useMutation({
    mutationFn: async (data: { identifier: string; otp: string; type: "email" | "phone" }) => {
      const response = await apiRequest('/api/auth/verify-otp', {
        method: 'POST',
        body: JSON.stringify(data),
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to verify OTP');
      }

      return response.json() as Promise<VerifyOtpResponse>;
    },
    onSuccess: (data) => {
      if (data.success && data.token) {
        // Store JWT token
        localStorage.setItem('authToken', data.token);

        toast({
          title: data.isNewUser ? "Welcome!" : "Login Successful!",
          description: data.message,
        });

        // Redirect to home or previous page
        const redirectTo = new URLSearchParams(window.location.search).get('redirect') || '/';
        navigate(redirectTo);
      } else {
        toast({
          title: "Error",
          description: data.message,
          variant: "destructive",
        });
      }
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSendOtp = () => {
    if (!identifier.trim()) {
      toast({
        title: "Error",
        description: `Please enter your ${authType === 'email' ? 'email' : 'phone number'}`,
        variant: "destructive",
      });
      return;
    }

    // Validate email format (Gmail only)
    if (authType === 'email' && !identifier.endsWith('@gmail.com')) {
      toast({
        title: "Error",
        description: "Please use a Gmail address",
        variant: "destructive",
      });
      return;
    }

    // Validate phone format
    if (authType === 'phone' && !identifier.startsWith('+')) {
      toast({
        title: "Error",
        description: "Please include country code (e.g., +1234567890)",
        variant: "destructive",
      });
      return;
    }

    sendOtpMutation.mutate({ identifier, type: authType });
  };

  const handleVerifyOtp = () => {
    if (otp.length !== 6) {
      toast({
        title: "Error",
        description: "Please enter the complete 6-digit OTP",
        variant: "destructive",
      });
      return;
    }

    verifyOtpMutation.mutate({ identifier, otp, type: authType });
  };

  const handleResendOtp = () => {
    if (timer > 0) return;
    sendOtpMutation.mutate({ identifier, type: authType });
  };

  const handleBackToIdentifier = () => {
    setStep("identifier");
    setOtp("");
    setTimer(0);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-purple-900 to-violet-800 flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        <Card className="bg-black/80 border-purple-500/30 shadow-2xl shadow-purple-500/20">
          <CardHeader className="text-center">
            <div className="flex items-center justify-center mb-4">
              <div className="relative">
                <Sparkles className="h-12 w-12 text-purple-400 animate-pulse" />
                <div className="absolute inset-0 bg-purple-400 rounded-full animate-ping opacity-20" />
              </div>
            </div>
            <CardTitle className="text-2xl font-bold bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent">
              GameHub Pro
            </CardTitle>
            <CardDescription className="text-gray-300">
              {step === "identifier" 
                ? "Enter your details to get started" 
                : "Enter the OTP sent to your " + authType}
            </CardDescription>
          </CardHeader>

          <CardContent className="space-y-6">
            {step === "identifier" ? (
              <>
                <Tabs value={authType} onValueChange={(value) => setAuthType(value as "email" | "phone")}>
                  <TabsList className="grid w-full grid-cols-2 bg-gray-800 border-gray-700">
                    <TabsTrigger value="email" className="flex items-center gap-2 data-[state=active]:bg-purple-600">
                      <Mail className="h-4 w-4" />
                      Gmail
                    </TabsTrigger>
                    <TabsTrigger value="phone" className="flex items-center gap-2 data-[state=active]:bg-purple-600">
                      <Phone className="h-4 w-4" />
                      Phone
                    </TabsTrigger>
                  </TabsList>

                  <TabsContent value="email" className="space-y-4">
                    <div className="space-y-2">
                      <Label htmlFor="email" className="text-gray-200">Gmail Address</Label>
                      <Input
                        id="email"
                        type="email"
                        placeholder="your.email@gmail.com"
                        value={identifier}
                        onChange={(e) => setIdentifier(e.target.value)}
                        className="bg-gray-800 border-gray-600 text-white placeholder:text-gray-400 focus:border-purple-500 focus:ring-purple-500"
                        onKeyDown={(e) => e.key === 'Enter' && handleSendOtp()}
                      />
                    </div>
                    <Alert className="bg-blue-900/50 border-blue-500/50">
                      <Mail className="h-4 w-4 text-blue-400" />
                      <AlertDescription className="text-blue-200">
                        We support Gmail addresses only. You'll receive a 6-digit code via email.
                      </AlertDescription>
                    </Alert>
                  </TabsContent>

                  <TabsContent value="phone" className="space-y-4">
                    <div className="space-y-2">
                      <Label htmlFor="phone" className="text-gray-200">Phone Number</Label>
                      <Input
                        id="phone"
                        type="tel"
                        placeholder="+1234567890"
                        value={identifier}
                        onChange={(e) => setIdentifier(e.target.value)}
                        className="bg-gray-800 border-gray-600 text-white placeholder:text-gray-400 focus:border-purple-500 focus:ring-purple-500"
                        onKeyDown={(e) => e.key === 'Enter' && handleSendOtp()}
                      />
                    </div>
                    <Alert className="bg-green-900/50 border-green-500/50">
                      <Phone className="h-4 w-4 text-green-400" />
                      <AlertDescription className="text-green-200">
                        Include country code (e.g., +1 for US). You'll receive a 6-digit code via SMS.
                      </AlertDescription>
                    </Alert>
                  </TabsContent>
                </Tabs>

                <Button
                  onClick={handleSendOtp}
                  disabled={sendOtpMutation.isPending}
                  className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white font-semibold py-2 px-4 rounded-lg transition-all duration-200 transform hover:scale-[1.02] disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {sendOtpMutation.isPending ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      Sending OTP...
                    </>
                  ) : (
                    <>
                      <Lock className="mr-2 h-4 w-4" />
                      Send OTP
                    </>
                  )}
                </Button>
              </>
            ) : (
              <>
                <div className="space-y-4">
                  <div className="text-center">
                    <p className="text-gray-300 mb-2">
                      OTP sent to:
                    </p>
                    <p className="font-semibold text-purple-400">
                      {authType === 'email' ? identifier : identifier.replace(/(\d{3})\d{4}(\d{3})/, '$1****$2')}
                    </p>
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="otp" className="text-gray-200">Enter 6-Digit OTP</Label>
                    <Input
                      id="otp"
                      type="text"
                      placeholder="123456"
                      value={otp}
                      onChange={(e) => setOtp(e.target.value.replace(/\D/g, '').slice(0, 6))}
                      className="bg-gray-800 border-gray-600 text-white placeholder:text-gray-400 focus:border-purple-500 focus:ring-purple-500 text-center text-lg tracking-widest"
                      maxLength={6}
                      onKeyDown={(e) => e.key === 'Enter' && handleVerifyOtp()}
                      autoFocus
                    />
                  </div>

                  <div className="flex justify-between items-center text-sm">
                    <Button
                      variant="ghost"
                      onClick={handleBackToIdentifier}
                      className="text-gray-400 hover:text-white p-0 h-auto"
                    >
                      â† Change {authType}
                    </Button>

                    <Button
                      variant="ghost"
                      onClick={handleResendOtp}
                      disabled={timer > 0 || sendOtpMutation.isPending}
                      className="text-purple-400 hover:text-purple-300 p-0 h-auto disabled:opacity-50"
                    >
                      {timer > 0 ? `Resend in ${timer}s` : 'Resend OTP'}
                    </Button>
                  </div>

                  <Button
                    onClick={handleVerifyOtp}
                    disabled={verifyOtpMutation.isPending || otp.length !== 6}
                    className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white font-semibold py-2 px-4 rounded-lg transition-all duration-200 transform hover:scale-[1.02] disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {verifyOtpMutation.isPending ? (
                      <>
                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                        Verifying...
                      </>
                    ) : (
                      'Verify & Login'
                    )}
                  </Button>
                </div>
              </>
            )}

            <div className="text-center text-sm text-gray-400">
              <p>
                New users get <span className="text-purple-400 font-semibold">1000 bonus points</span> upon registration!
              </p>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/topup.tsx.content:
import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import Navigation from "@/components/navigation";
import { CoinsIcon, CreditCardIcon, DollarSignIcon, CrownIcon } from "lucide-react";

interface TopUpPackage {
  id: string;
  name: string;
  price: number;
  points: number;
  bonus?: number;
  popular?: boolean;
  icon: React.ReactNode;
}

const packages: TopUpPackage[] = [
  {
    id: 'starter',
    name: 'Starter Pack',
    price: 50,
    points: 500,
    icon: <CoinsIcon className="w-8 h-8 text-primary" />,
  },
  {
    id: 'value',
    name: 'Value Pack',
    price: 100,
    points: 1100,
    bonus: 100,
    popular: true,
    icon: <DollarSignIcon className="w-8 h-8 text-accent" />,
  },
  {
    id: 'premium',
    name: 'Premium Pack',
    price: 200,
    points: 2500,
    bonus: 300,
    icon: <CrownIcon className="w-8 h-8 text-secondary" />,
  },
];

export default function TopUp() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: balanceData } = useQuery({
    queryKey: ['/api/balance'],
  });

  const topUpMutation = useMutation({
    mutationFn: async (packageId: string) => {
      const response = await apiRequest('POST', '/api/topup', {
        package: packageId,
      });
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Top-up Successful!",
        description: `${data.transaction.amount} points added to your balance.`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/balance'] });
      queryClient.invalidateQueries({ queryKey: ['/api/auth/user'] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Top-up Failed",
        description: error.message || "Failed to process payment",
        variant: "destructive",
      });
    },
  });

  const handlePurchase = (packageId: string) => {
    topUpMutation.mutate(packageId);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-dark via-surface to-dark">
      <Navigation />

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold mb-2 text-white">Top Up Points ðŸ’³</h1>
          <p className="text-gray-400">Add more points to continue playing!</p>
        </div>

        {/* Current Balance */}
        <Card className="bg-surface/80 border-surface-light backdrop-blur-sm mb-8">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-400 text-sm">Current Balance</p>
                <p className="text-3xl font-bold text-accent">
                  {balanceData?.balance || 0} Points
                </p>
              </div>
              <CoinsIcon className="h-12 w-12 text-accent opacity-60" />
            </div>
          </CardContent>
        </Card>

        <div className="max-w-4xl">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
            {packages.map((pkg) => (
              <Card key={pkg.id} className={`bg-surface/80 border-surface-light backdrop-blur-sm hover:border-primary transition-colors relative ${pkg.popular ? 'border-accent' : ''}`}>
                {pkg.popular && (
                  <div className="absolute -top-3 left-1/2 transform -translate-x-1/2">
                    <Badge className="bg-accent text-white">
                      POPULAR
                    </Badge>
                  </div>
                )}
                <CardContent className="p-6 text-center">
                  <div className="bg-primary/20 rounded-full w-16 h-16 flex items-center justify-center mx-auto mb-4">
                    {pkg.icon}
                  </div>
                  <CardTitle className="text-xl font-bold mb-2 text-white">{pkg.name}</CardTitle>
                  <p className="text-3xl font-bold text-primary mb-2">â‚¹{pkg.price}</p>
                  <p className="text-gray-400 mb-2">{pkg.points} Points</p>
                  {pkg.bonus && (
                    <p className="text-xs text-accent mb-4">+{pkg.bonus} Bonus Points</p>
                  )}
                  <Button 
                    onClick={() => handlePurchase(pkg.id)}
                    disabled={topUpMutation.isPending}
                    className={`w-full font-semibold py-3 px-4 rounded-lg transition-opacity ${
                      pkg.popular 
                        ? 'bg-accent text-white hover:bg-accent/90' 
                        : pkg.id === 'premium'
                        ? 'bg-secondary text-white hover:bg-secondary/90'
                        : 'bg-primary text-white hover:bg-primary/90'
                    }`}
                  >
                    {topUpMutation.isPending ? 'Processing...' : 'Purchase'}
                  </Button>
                </CardContent>
              </Card>
            ))}
          </div>

          {/* Payment Methods */}
          <Card className="bg-surface/80 border-surface-light backdrop-blur-sm">
            <CardHeader>
              <CardTitle className="text-xl font-semibold text-white">Payment Methods</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                <div className="flex items-center justify-center p-4 bg-surface-light rounded-lg">
                  <CreditCardIcon className="w-6 h-6 text-blue-500 mr-2" />
                  <span className="font-medium text-white">Visa</span>
                </div>
                <div className="flex items-center justify-center p-4 bg-surface-light rounded-lg">
                  <CreditCardIcon className="w-6 h-6 text-red-500 mr-2" />
                  <span className="font-medium text-white">Mastercard</span>
                </div>
                <div className="flex items-center justify-center p-4 bg-surface-light rounded-lg">
                  <div className="w-6 h-6 bg-blue-600 rounded mr-2"></div>
                  <span className="font-medium text-white">PayPal</span>
                </div>
                <div className="flex items-center justify-center p-4 bg-surface-light rounded-lg">
                  <div className="w-6 h-6 bg-green-500 rounded mr-2"></div>
                  <span className="font-medium text-white">UPI</span>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </main>
    </div>
  );
}

